#include <Arduino.h>
#include <WiFi.h>
#include <ESPAsyncWebServer.h>
#include <ESP32Servo.h>

const char* ssid = "Your_ssid";
const char* password = "your_password";

const int servo_pin = 21;

IPAddress ip(192, 168, 0, 1);
IPAddress mask(255, 255, 255, 0);

AsyncWebServer s(80);

const char* content = R"rawliteral(
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Servo Motor</title>
    <style>
        html, body {
            overflow: hidden;
            overscroll-behavior: none;
            background-color: #000;
            height: 100%;
        }

        ::backdrop {
            background-color: #000;
        }

        #rotate-image {
            position: absolute;
            top: 12.5%;
            left: 0%;
            height: 25%;
            width: 100%;
            transition: transform 0.1s;
        }

        #range {
            -webkit-appearance: none;
            appearance: none;
            position: absolute;
            background-size: 100%;
            background-color: aqua;
            border-radius: 10px;
            height: 15px;
            width: 80%;
            opacity: 0.7;
            top: 50%;
            left: 10%;
        }

        #range::-webkit-slider-thumb {
            -webkit-appearance: none;
            appearance: none;
            background-color: #00f;
            border: 1px solid aqua;
            border-radius: 50%;
            width: 25px;
            height: 25px;
            cursor: pointer;
        }

        #output {
            position: absolute;
            color: #fff;
            top: 60%;
            left: 40%;
        }

        #rf {
            position: absolute;
            top: 90%;
            left: 47%;
            background-color: green;
            width: 6%;
            height: 1%;
            margin: 2% auto;
        }
    </style>
</head>
<body>
    <img id="rotate-image" src="data:image/jpeg;base64," alt="Rotatable Image">
    <input type="range" id="range" min="0" max="180" step="1" value="0">
    <div id="output">0<sup>0</sup> degree</div>
    <div id="rf" onclick="rf()"></div>
    <script>
function sendata(d){
    fetch("/degree", {
        method: "POST",
        headers: {
            'Accept': 'text/plain',
            'Content-Type': 'text/plain'
        },
        body: d
    }).then(res => {
        return res.text();
    }).then(b => {

    }).catch(e => {
        
    });
}

const body = document.body;
const image = document.getElementById('rotate-image');
var degree = document.getElementById("range");
var output = document.getElementById("output");
var angle = "0";

degree.addEventListener("touchstart", (e) => {
    degree.style.opacity = "1";
});

degree.addEventListener("touchmove", (e) => {
    angle = String(degree.value);
    output.innerHTML = angle + "<sup>0</sup> degree";
    image.style.transform = `rotate(${-1*parseInt(angle)}deg)`;
});

degree.addEventListener("touchend", (e) => {
    degree.style.opacity = "0.7";
    angle = String(degree.value);
    output.innerHTML = angle + "<sup>0</sup> degree";
    image.style.transform = `rotate(${-1*parseInt(angle)}deg)`;
    sendata("data=" + angle);
});

function rf() {
    body.requestFullscreen();
}
    </script>
</body>
</html>
)rawliteral";


Servo servo;

String data;


void setup() {
  WiFi.mode(WIFI_AP);
  WiFi.softAP(ssid, password);
  WiFi.softAPConfig(ip, ip, mask);
  servo.attach(servo_pin);

  s.on("/", HTTP_GET, [](AsyncWebServerRequest *req){
    req->send_P(200, "text/html", content);
  });

  s.on("/degree", HTTP_POST, [](AsyncWebServerRequest *req){
    if (req->hasParam("data", true)){
        data = req->getParam("data", true)->value();
        servo.write(data.toInt());
        
        req->send_P(200, "text/plain", "Received!");
    } else {
      req->send_P(400, "text/plain", "ERROR");
    }
  });

  s.begin();
  
}

void loop() {
  
}